import React, { useState } from "react";
import "./bio-card.component.css";

function BioCardComponent() {
  const [name, setName] = useState("Marie Curie");
  const [job, setJob] = useState("Scientist");
  const [description, setDescription] = useState(
    "This is a brief description about so and so "
  );

  return (
    <div className="bio-card">
      <div
        className="upper-container"
        styles={{
          backgroundImage: `url(./bio-card/Marie-Curie-1-CROPPED.jpg)`,
        }}
      ></div>
      <div className="lower-container">
        <h3>{name}</h3>
        <h4>{job}</h4>
        <p>{description}</p>
        <button className="show-full-bio">LEARN MORE!</button>
      </div>
    </div>
  );
}

export default BioCardComponent;
<div className="image-container">
  <image
    src="minerva-project-2\src\images\logos\beaker.png"
    alt="logo picture"
    height="70px"
    width="70px"
  />
</div>;
