import React, { useState } from "react";
import "./bio-card.component.css";

function BioCardComponent() {
  const [name, setName] = useState("Marie Curie");
  const [job, setJob] = useState("Scientist");
  const [description, setDescription] = useState(
    "This is a brief description about so and so "
  );

  return (
    <div className="bio-card">
      <div className="upper-container">
        <div className="image-container">
          <image
            src="minerva-project-2\src\images\logos\kisspng-beaker-laboratory-flask-clip-art-science-png-image-5a752ec4e39bf3.5454071215176291249323.png"
            alt="logo picture"
            height="100px"
            width="100px"
          />
        </div>
      </div>
      <div className="lower-container">
        <h3>{name}</h3>
        <h4>{job}</h4>
        <p>{description}</p>
        <button className="show-full-bio">Learn More!</button>
      </div>
    </div>
  );
}

export default BioCardComponent;
